public struct BucketIdSet: OptionSet {
    public typealias RawValue = UInt64

    public static let empty: BucketIdSet = []

    public var rawValue: RawValue

    public init(rawValue: RawValue) {
        self.rawValue = rawValue
    }

    public init(element: BucketId) {
        self.rawValue = UInt64(0b1) << element.rawValue
    }
}
