    @Args("filter", { type: () => BrandFilterInput, nullable: true }) filter: BrandFilterInput,
    @Args("pageSize", { type: () => Int, nullable: true }) pageSize: number,
    @Args("currentPage", { type: () => Int, nullable: true }) currentPage: number,
  ) {
