public class HouseBuilding {
    static int getMinimum(String[] area) {
        int res = Integer.MAX_VALUE;
        final int MIN_LEVEL = 0;
        final int MAX_LEVEL = 9;
        for (int level = MIN_LEVEL; level < MAX_LEVEL; level++) {
            res = Math.min(res, calculateEffortForLevel(level, area));
        }
        return res;
    }

    static int calculateEffortForLevel(int level, String[] area) {
        int effort = 0;
        for (String row : area)
            for (int position = 0; position < row.length(); position ++) {
                int height = getHeightValue(row, position);
                effort += Math.min(Math.abs(level - height), Math.abs(level - height + 1));
            }
        return effort;
    }

    private static int getHeightValue(String row, int position) {
        return row.charAt(position) - '0';
    }
}

