 public NeuralNetwork(int numInputs, int numHiddenNodes, int numOutputs) {
 this.numInputs = numInputs;
 this.numHiddenNodes = numHiddenNodes;
 this.numOutputs = numOutputs;

 inputToHiddenWeights = new float[numInputs, numHiddenNodes];
 hiddenToOutputWeights = new float[numHiddenNodes, numOutputs];
 hiddenBiases = new float[numHiddenNodes];
 outputBiases = new float[numOutputs];

 // Initialize the weights and biases with random values:
 InitializeWeightsAndBiases();
 }

 public float[] Compute(float[] inputs) {
 // Compute the output values for the given input values:
 float[] hiddenValues = new float[numHiddenNodes];
 float[] outputs = new float[numOutputs];

 for (int i = 0; i < numHiddenNodes; i++) {
 float sum = 0.0f;
 for (int j = 0; j < numInputs; j++) {
 sum += inputs[j] * inputToHiddenWeights[j, i];
 }
 sum += hiddenBiases[i];
 hiddenValues[i] = Sigmoid(sum);
 }

 for (int i = 0; i < numOutputs; i++) {
 float sum = 0.0f;
 for (int j = 0; j < numHiddenNodes; j++) {
 sum += hiddenValues[j] * hiddenToOutputWeights[j, i];
 }
 sum += outputBiases[i];
 outputs[i] = Sigmoid(sum);
 }

 return outputs;
 }

 private void InitializeWeightsAndBiases() {
 for (int i = 0; i < numInputs; i++) {
 for (int j = 0; j < numHiddenNodes; j++) {
 inputToHiddenWeights[i, j] = Random.Range(-1.0f, 1.0f);
 }
 }

 for (int i = 0; i < numHiddenNodes; i++) {
 for (int j = 0; j < numOutputs; j++) {
 hiddenToOutputWeights[i, j] = Random.Range(-1.0f, 1.0f);
 }
