 hiddenBiases[i] = Random.Range(-1.0f, 1.0f);
 }

 for (int i = 0; i < numOutputs; i++) {
 outputBiases[i] = Random.Range(-1.0f, 1.0f);
 }
 }

 private float Sigmoid(float x) {
 return 1.0f / (1.0f + Mathf.Exp(-x));
 }
}
