```
pragma solidity ^0.8.0;

contract OffRamp {
 mapping(address => uint256) public balances;
 address public owner;

 constructor() public {
 owner = msg.sender;
 }

 function deposit() public payable {
 require(msg.value > 0, "Deposit amount must be greater than 0");
 balances[msg.sender] += msg.value;
 }

 function withdraw(uint256 _amount) public {
 require(_amount > 0, "Withdrawal amount must be greater than 0");
 require(balances[msg.sender] >= _amount, "Insufficient funds");
 require(msg.sender.transfer(_amount), "Transfer failed");
 balances[msg.sender] -= _amount;
 }

 function convertToFiat(address _recipient, uint256 _amount) public {
 require(_amount > 0, "Conversion amount must be greater than 0");
 require(balances[msg.sender] >= _amount, "Insufficient funds");
 require(msg.sender == owner || msg.sender == _recipient, "Unauthorized");
 require(_recipient.transfer(_amount), "Transfer failed");
 balances[msg.sender] -= _amount;
 }

 function getBalance() public view returns (uint256) {
 return balances[msg.sender];
 }
}
```
