@RequiredArgsConstructor
public class ApiConsumer {

 private final WebClient webClient;
 private final KafkaTemplate<String, String> kafkaTemplate;
 private final MongoTemplate mongoTemplate;

 @Scheduled(cron = "0 0/15 \* \* \* \*", zone = "Asia/Kolkata")
 public void consumeApis() {
 try {
 List<ApiDetails> apiDetailsList = mongoTemplate.findAll(ApiDetails.class);
 apiDetailsList.forEach(apiDetails -> {
 String url = apiDetails.getEndpoint();
 String username = apiDetails.getUsername();
 String password = apiDetails.getPassword();
 String authCode = apiDetails.getAuthorizationCode();

 WebClient.RequestBodySpec requestBodySpec = webClient.get()
 .uri(url)
 .retrieve()
 .onStatus(HttpStatus::isError, response -> {
 log.error("Failed to retrieve data from API: {}", response.bodyToMono(String.class));
 throw new RuntimeException("Failed to retrieve data from API");
 });

 if (StringUtils.isNotBlank(username) && StringUtils.isNotBlank(password)) {
 requestBodySpec = requestBodySpec
 .header(HttpHeaders.AUTHORIZATION, "Basic " + getBasicAuth(username, password));
 } else if (StringUtils.isNotBlank(authCode)) {
 requestBodySpec = requestBodySpec
 .header(HttpHeaders.AUTHORIZATION, "Bearer " + authCode);
 }

 String responseBody = requestBodySpec
 .bodyToMono(String.class)
 .block();

 if (StringUtils.isNotBlank(responseBody)) {
 List<String> filteredFields = Arrays.asList("field1", "field2", "field3");
 JSONArray jsonArray = new JSONArray(responseBody);

 List<ApiData> apiDataList = new ArrayList<>();
 for (int i = 0; i < jsonArray.length(); i++) {
 JSONObject jsonObject = jsonArray.getJSONObject(i);

 ApiData apiData = new ApiData();
 apiData.setCompositeId(jsonObject.getString("id"));
 apiData.setDescription(jsonObject.getString("description"));

 for (String field : filteredFields) {
 if (jsonObject.has(field)) {
 apiData.getData().put(field, jsonObject.getString(field));
 }
 }

 apiDataList.add(apiData);
 }

 mongoTemplate.insertAll(apiDataList);

 kafkaTemplate.send("api-data", new ObjectMapper().writeValueAsString(apiDataList));
 }
 });
 } catch (Exception e) {
 log.error("Error while consuming APIs", e);
 sendErrorEmail(e);
 }
 }

 private String getBasicAuth(String username, String password) {
 String auth = username + ":" + password;
 byte[] encodedAuth = Base64.getEncoder().encode(auth.getBytes(Charset.forName("US-ASCII")));
 return new String(encodedAuth);
 }

 private void sendErrorEmail(Exception e) {
 // Code to send error email
 }

}

