from sklearn.base import BaseEstimator, RegressorMixin
from scipy.optimize import curve_fit


def arps_equation(t, q_i, D, b):
    return q_i * ((1 + D) / (1 + b * D)) ** (b * t)


class ARPSEstimator(BaseEstimator, RegressorMixin):
    def __init__(self):
        pass

    def fit(self, X, y):
        q_i_min = y.median()
        q_i_max = y.max()
        b_min = 0.01
        b_max = 0.5
        D_min = 0.01
        D_max = 0.6

        self.lower_bounds = [q_i_min, b_min, D_min]
        self.upper_bounds = [q_i_max, b_max, D_max]

        self.optimal_params, _ = curve_fit(
            arps_equation, X, y, bounds=(self.lower_bounds, self.upper_bounds)
        )
        return self

    def predict(self, X):
        return arps_equation(X, *self.optimal_params)

    def score(self, X, y):
        y_pred = self.predict(X)
        return 1 - ((y - y_pred) ** 2).mean() / y.var()

