 "fmt"
 "sync"
 "time"
)

const maxRPS = 1000

type rateLimiter struct {
 mu sync.Mutex
 startTime time.Time
 count int
}

func (r \*rateLimiter) allow() bool {
 r.mu.Lock()
 defer r.mu.Unlock()

 now := time.Now()
 if now.Sub(r.startTime) > time.Second {
 r.startTime = now
 r.count = 0
 }
 r.count++
 return r.count <= maxRPS
}

