function createWeeklyCopy() {
 // Replace the document IDs with the IDs of the source and destination documents
 var sourceDocId = "INSERT_SOURCE_DOCUMENT_ID";
 var destinationDocId = "INSERT_DESTINATION_DOCUMENT_ID";

 // Get the source document and its body
 var sourceDoc = DocumentApp.openById(sourceDocId);
 var sourceBody = sourceDoc.getBody();

 // Create a new copy of the source document
 var newDocName = "Weekly Copy " + Utilities.formatDate(new Date(), Session.getScriptTimeZone(), "yyyy-MM-dd");
 var newDoc = DocumentApp.create(newDocName);

 // Get the destination document and its body
 var destinationDoc = DocumentApp.openById(destinationDocId);
 var destinationBody = destinationDoc.getBody();

 // Copy the contents of the source document to the destination document
 var elements = sourceBody.getNumChildren();
 for (var i = 0; i < elements; i++) {
 var element = sourceBody.getChild(i).copy();
 var type = element.getType();
 if (type == DocumentApp.ElementType.PARAGRAPH) {
 destinationBody.appendParagraph(element);
 } else if (type == DocumentApp.ElementType.TABLE) {
 destinationBody.appendTable(element);
 } else if (type == DocumentApp.ElementType.LIST_ITEM) {
 destinationBody.appendListItem(element);
 }
 }

 // Move the new copy of the source document to the destination document
 var newDocId = newDoc.getId();
 DriveApp.getFileById(newDocId).moveTo(destinationDocId);
}
```
