import re

class URLValidator:
 def \_\_init\_\_(self, valid\_schemes=None, disallowed\_schemes=None, valid\_domains=None, 
 disallowed\_domains=None, valid\_extensions=None, disallowed\_extensions=None,
 valid\_years=None, disallowed\_years=None):
 self.valid_schemes = valid_schemes or ['http', 'https']
 self.disallowed_schemes = disallowed_schemes or []
 self.valid_domains = valid_domains or []
 self.disallowed_domains = disallowed_domains or []
 self.valid_extensions = valid_extensions or []
 self.disallowed_extensions = disallowed_extensions or []
 self.valid_years = valid_years or []
 self.disallowed_years = disallowed_years or []

 def is\_valid\_scheme(self, url):
 return url.scheme in self.valid_schemes and url.scheme not in self.disallowed_schemes

 def is\_valid\_domain(self, url):
 return (not self.valid_domains or url.netloc in self.valid_domains) and url.netloc not in self.disallowed_domains

 def is\_valid\_extension(self, url):
 if not self.valid_extensions and not self.disallowed_extensions:
 return True
 path = url.path.lower()
 if self.valid_extensions:
 for ext in self.valid_extensions:
 if path.endswith(ext):
 return True
 return False
 else:
 for ext in self.disallowed_extensions:
 if path.endswith(ext):
 return False
 return True

 def is\_valid\_year(self, url):
 if not self.valid_years and not self.disallowed_years:
 return True
 path = url.path.lower()
 if self.valid_years:
 for year in self.valid_years:
 if str(year) in path:
 return True
 return False
 else:
 for year in self.disallowed_years:
 if str(year) in path:
 return False
 return True

 def is\_valid(self, url):
 return (self.is_valid_scheme(url) and self.is_valid_domain(url) and 
 self.is_valid_extension(url) and self.is_valid_year(url))
