class FooBarTextInput extends HTMLElement {
 private readonly \_input: HTMLInputElement;
 private readonly \_internals: ElementInternals;

 constructor() {
 super();
 this.attachShadow({ mode: "open" });

 this.\_input = document.createElement("input");
 this.\_input.setAttribute("type", "text");

 this.\_internals = this.attachInternals();
 this.\_internals.setFormValue(this.\_input.value);
 this.\_input.addEventListener("input", () => {
 this.\_internals.setFormValue(this.\_input.value);
 this.\_validate();
 });
 }

 private \_validate() {
 const validityState = this.\_input.validity;
 const errorMessage = this.getAttribute("error-message");

 if (validityState.customError) {
 this.\_internals.setValidity({ customError: true }, errorMessage);
 } else {
 this.\_internals.setValidity(validityState, errorMessage);
 }
 }

 static get observedAttributes() {
 return ["required", "pattern"];
 }

 attributeChangedCallback(name: string, oldValue: string, newValue: string) {
 if (name === "required") {
 this.\_input.required = newValue !== null;
 } else if (name === "pattern") {
 this.\_input.pattern = newValue;
 }
 this.\_validate();
 }

 connectedCallback() {
 this.shadowRoot?.append(this.\_input);
 this.\_validate();
 }
}

customElements.define("foo-bar-text-input", FooBarTextInput);
