func solution(grid [][]string) bool {
 // check rows
 for i := 0; i < 9; i++ {
 row := make(map[string]bool)
 for j := 0; j < 9; j++ {
 if grid[i][j] != "." {
 if row[grid[i][j]] {
 return false
 }
 row[grid[i][j]] = true
 }
 }
 }

 // check columns
 for i := 0; i < 9; i++ {
 col := make(map[string]bool)
 for j := 0; j < 9; j++ {
 if grid[j][i] != "." {
 if col[grid[j][i]] {
 return false
 }
 col[grid[j][i]] = true
 }
 }
 }

 // check sub-grids
 for i := 0; i < 9; i += 3 {
 for j := 0; j < 9; j += 3 {
 subgrid := make(map[string]bool)
 for m := i; m < i+3; m++ {
 for n := j; n < j+3; n++ {
 if grid[m][n] != "." {
 if subgrid[grid[m][n]] {
 return false
 }
 subgrid[grid[m][n]] = true
 }
 }
 }
 }
 }

 return true
}
```
