```javascript
function convertToBezier(obj, error = 0.001, maxCurves = null) {
 const points = obj.points;
 const curve = new Bezier(points);
 let curves = curve.getLUT(error, maxCurves);

 // Flatten the control points array
 let controlPoints = [];
 curves.forEach(c => {
 controlPoints.push(c[0]);
 controlPoints.push(c[1]);
 controlPoints.push(c[2]);
 });
 controlPoints.push(curves[curves.length - 1][2]);

 return controlPoints;
}
