function popOutElement(element) {
 // Get the current position and dimensions of the element
 const rect = element.getBoundingClientRect();
 const prevWidth = rect.width;
 const prevHeight = rect.height;
 const prevLeft = rect.left;
 const prevTop = rect.top;

 // Create an empty placeholder element to replace the original
 const placeholder = document.createElement("div");
 placeholder.style.width = prevWidth + "px";
 placeholder.style.height = prevHeight + "px";
 placeholder.style.position = "absolute";
 placeholder.style.left = prevLeft + "px";
 placeholder.style.top = prevTop + "px";
 document.body.appendChild(placeholder);

 // Calculate the aspect ratio of the element
 const aspectRatio = prevWidth / prevHeight;

 // Get the width and height of the viewport
 const viewportWidth = window.innerWidth;
 const viewportHeight = window.innerHeight;

 // Calculate the new width and height, keeping the aspect ratio
 let newWidth, newHeight;
 if (viewportWidth / aspectRatio <= viewportHeight) {
 newWidth = viewportWidth;
 newHeight = viewportWidth / aspectRatio;
 } else {
 newHeight = viewportHeight;
 newWidth = viewportHeight * aspectRatio;
 }

 // Calculate the new position
 const newLeft = (viewportWidth - newWidth) / 2;
 const newTop = (viewportHeight - newHeight) / 2;

 // Set the position and size of the element to fill the viewport
 element.style.position = "fixed";
 element.style.width = newWidth + "px";
 element.style.height = newHeight + "px";
 element.style.left = newLeft + "px";
 element.style.top = newTop + "px";

 // Animate the transition from the previous position to the new position
 element.style.transition = "all 0.3s ease-out";
 setTimeout(() => {
 element.style.left = newLeft + "px";
 element.style.top = newTop + "px";
 element.style.width = newWidth + "px";
 element.style.height = newHeight + "px";
 }, 0);

 // Return a function to return the element to its previous position
 return function () {
 // Animate the transition back to the previous position
 element.style.transition = "all 0.3s ease-out";
 setTimeout(() => {
 element.style.position = "absolute";
 element.style.left = prevLeft + "px";
 element.style.top = prevTop + "px";
 element.style.width = prevWidth + "px";
 element.style.height = prevHeight + "px";
 }, 0);

 // Remove the placeholder element and re-add the original element to its original parent
 placeholder.remove();
 element.parentNode.appendChild(element);
 };
}
