 }
 const d = GeometryUtilities.dotProduct(planeNormal, l) / dotProduct;
 return {
 x: linePoint.x - d \* lineVector.x,
 y: linePoint.y - d \* lineVector.y,
 z: linePoint.z - d \* lineVector.z
 };
}

