}

// Helper function to check if a page is in the cache
isPageInCache(page) {
 return cache.contains(page);
}

// Helper function to display a page from the cache
displayPageFromCache(page) {
 displayPage(page, cache.get(page));
}

// Helper function to send a page request to the server
sendPageRequestToServer(page) {
 // Construct a new request object
 request = new XMLHttpRequest();
 request.open("GET", page.url, true);

 // Set up a callback for when the request completes
 request.onreadystatechange = function() {
 if (request.readyState === 4 && request.status === 200) {
 // Display the page that was retrieved from the server
 displayPage(page, request.responseText);
 }
 };

 // Send the request to the server
 request.send();
}

