export class PythonASTController {
  async getDependenciesForFile(
    fileName: string,
    fileNameWithExt: string,
    fileText: string,
    allFilePaths: string[]
  ): Promise<string[]> {
    var deps: string[] = [];
    try {
      if (fileNameWithExt.endsWith(".py")) {
        const exec = util.promisify(childProcess.exec);
        const { stdout, stderr } = await exec(
          `python3 ${process.cwd()}/src/server/controllers/extract-python-ast.py ${fileNameWithExt} '${fileText.replaceAll(
            "'",
            '"'
          )}'`
        );
        deps = stdout.split("\n").filter((str) => str !== "");
      } else {
        return [];
      }
    } catch (e) {
      console.log(e);
    }

    const adjustedDeps: string[] = [];
    for (const dep of deps) {
      let foundAbsPath = false;
      for (const path of allFilePaths) {
        if (path.includes(dep)) {
          adjustedDeps.push(path);
          foundAbsPath = true;
          break;
        }
      }
      if (!foundAbsPath) {
        adjustedDeps.push(dep);
      }
    }

    return adjustedDeps;
  }
}

