// Encode data using Huffman coding
function huffman\_encode($data) {
 // Build frequency table
 $freq = array\_count\_values(str\_split($data));
 asort($freq);

 // Build Huffman tree
 $tree = array();
 while (count($freq) > 1) {
 $a = key($freq);
 $b = next($freq);
 foreach (array($a, $b) as $node) {
 unset($freq[$node]);
 }
 $freq[$a . $b] = $freq[$a] + $freq[$b];
 arsort($freq);
 $tree[$a . $b] = array($a, $b);
 }

 // Build encoding table
 $table = array();
 $build\_table = function($node, $code = '') use (&$table, &$build\_table, $tree) {
 if (isset($tree[$node])) {
 $build\_table($tree[$node][0], $code . '0');
 $build\_table($tree[$node][1], $code . '1');
 } else {
 $table[$node] = $code;
 }
 };
 $root = key($freq);
 $build\_table($root);

 // Encode data
 $result = '';
 foreach (str\_split($data) as $char) {
 $result .= $table[$char];
 }
 return $result;
}

// Decode data using Huffman coding
function huffman\_decode($data, $tree) {
 $node = key($tree);
 $result = '';
 while ($node) {
 if (isset($tree[$node][0])) {
 $node = $tree[$node][$data[0]];
 $data = substr($data, 1);
 } else {
 $result .= $node;
 $node = key($tree);
 }
 }
 return $result;
}

// Example
