JavaScript:


```bash
const selfDrivingBtn = document.getElementById("self-driving-btn");
const needSeatBtn = document.getElementById("need-seat-btn");
const contentDiv = document.getElementById("content");

selfDrivingBtn.addEventListener("click", () => {
 contentDiv.innerHTML = `
 <h2>Self-Driving Form</h2>
 <form id="self-driving-form">
 <label for="name">Name:</label>
 <input type="text" id="name" name="name" required><br><br>
 <label for="seats">Number of Seats Available:</label>
 <input type="number" id="seats" name="seats" min="1" max="4" required><br><br>
 <input type="submit" value="Submit">
 </form>
 <div id="self-driving-table"></div>
 `;

 const selfDrivingTable = document.getElementById("self-driving-table");
 const selfDrivingForm = document.getElementById("self-driving-form");

 selfDrivingForm.addEventListener("submit", (e) => {
 e.preventDefault();

 const formData = new FormData(selfDrivingForm);
 const name = formData.get("name");
 const seats = formData.get("seats");

 const tableRow = document.createElement("tr");
 const nameTd = document.createElement("td");
 const seatsTd = document.createElement("td");

 nameTd.innerText = name;
 seatsTd.innerText = seats;

 tableRow.appendChild(nameTd);
 tableRow.appendChild(seatsTd);

 selfDrivingTable.appendChild(tableRow);

 selfDrivingForm.reset();
 });
});

needSeatBtn.addEventListener("click", () => {
 contentDiv.innerHTML = `
 <h2>Available Car Seats</h2>
 <table id="car-seats">
 <thead>
 <tr>
 <th>Name</th>
 <th>Number of Seats Available</th>
 </tr>
 </thead>
 <tbody>
 <tr>
 <td>John</td>
 <td>2</td>
 </tr>
 <tr>
 <td>Jane</td>
 <td>1</td>
 </tr>
 <tr>
 <td>Bob</td>
 <td>4</td>
 </tr>
 </tbody>
 </table>
 `;

 const carSeatsTable = document.getElementById("car-seats");

 carSeatsTable.addEventListener("click", (e) => {
 if (e.target.tagName === "TD") {
 const selectedRow = e.target.parentNode;
 const selectedName = selectedRow.querySelector("td:first-child").innerText;
 const selectedSeats = selectedRow.querySelector("td:last-child").innerText;

 if (confirm(`Confirm selection of ${selectedName} with ${selectedSeats} available seats?`)) {
 selectedRow.remove();
 }
 }
 });
});
