// Check connection
if ($conn->connect_error) {
 die(json\_encode(['status' => 'error', 'message' => 'Connection failed: ' . $conn->connect_error]));
}

// Get POST data
$data = json\_decode(file\_get\_contents("php://input"), true);
$mobile = mysqli\_real\_escape\_string($conn, $data['mobile']);

// Check if the mobile number exists in the efendim\_survey table
$sql = "SELECT COUNT(\*) as count FROM efendim\_survey WHERE mobile = '$mobile'";
$result = $conn->query($sql);

if ($result->num_rows > 0) {
 $row = $result->fetch\_assoc();
 if ($row['count'] > 0) {
 echo json\_encode(['status' => 'exists', 'message' => 'Customer has already taken the survey']);
 } else {
 echo json\_encode(['status' => 'not\_exists', 'message' => 'Customer has not taken the survey']);
 }
} else {
 echo json\_encode(['status' => 'error', 'message' => 'Error: ' . $sql . '<br>' . $conn->error]);
}

$conn->close();
?>
```
