use App\Entity\ChatMessage;
use Ratchet\Http\HttpServer;
use Ratchet\Server\IoServer;
use Ratchet\WebSocket\WsServer;
use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;
use Symfony\Component\HttpFoundation\Request;
use Symfony\Component\HttpFoundation\Response;
use Symfony\Component\Routing\Annotation\Route;

class ChatController extends AbstractController
{
 /\*\*
 \* @Route("/chat", name="chat")
 \*/
 public function chat(): Response
 {
 // display a list of recent chat conversations
 $conversations = $this->getDoctrine()
 ->getRepository(ChatMessage::class)
 ->findRecentConversations($this->getUser());

 return $this->render('chat/index.html.twig', [
 'conversations' => $conversations,
 ]);
 }

 /\*\*
 \* @Route("/chat/{otherUser}", name="chat\_conversation")
 \*/
 public function conversation(Request $request, $otherUser): Response
 {
 // get the current user and the other user for this conversation
 $user = $this->getUser();
 $otherUser = $this->getDoctrine()
 ->getRepository(User::class)
 ->findOneByUsername($otherUser);

 // get the chat messages for this conversation
 $messages = $this->getDoctrine()
 ->getRepository(ChatMessage::class)
 ->findConversationMessages($user, $otherUser);

 // handle the form submission to send a new message
 $form = $this->createForm(ChatMessageType::class);
 $form->handleRequest($request);
 if ($form->isSubmitted() && $form->isValid()) {
 $message = $form->getData();
 $message->setSender($user);
 $message->setRecipient($otherUser);
 $entityManager = $this->getDoctrine()->getManager();
 $entityManager->persist($message);
 $entityManager->flush();

 // send the new message to the other user using WebSocket
 $pusher = $this->get('app.chat\_pusher');
 $pusher->pushMessage($message);

 return $this->redirectToRoute('chat\_conversation', [
 'otherUser' => $otherUser->getUsername(),
 ]);
 }

 return $this->render('chat/conversation.html.twig', [
 'otherUser' => $otherUser,
 'messages' => $messages,
 'form' => $form->createView(),
 ]);
 }

 /\*\*
 \* @Route("/chat/socket", name="chat\_socket")
 \*/
 public function socket()
 {
 $pusher = $this->get('app.chat\_pusher');
 $server = IoServer::factory(new HttpServer(new WsServer($pusher)), 8080);
 $server->run();
 }
}
