 public TurnOnLightCommand(Light light) {
 this.light = light;
 }

 @Override
 public void execute() {
 light.turnOn();
 }
}

class TurnOffLightCommand implements Command {
 private Light light;

 public TurnOffLightCommand(Light light) {
 this.light = light;
 }

 @Override
 public void execute() {
 light.turnOff();
 }
}

