 System.out.println("Turning on the light");
 }

 public void turnOff() {
 System.out.println("Turning off the light");
 }
}

class RemoteControl {
 private Command command;

 public void setCommand(Command command) {
 this.command = command;
 }

 public void pressButton() {
 command.execute();
 }
}

// Usage
