 public Transform headset;
 public Transform leftController;
 public Transform rightController;
 public Transform hips;

 public float bobbingSpeed = 0.1f;
 public float bobbingAmount = 0.2f;
 public float weavingSpeed = 0.1f;
 public float weavingAmount = 0.2f;

 private Animator animator;
 private bool isBowing;
 private float bobOffset;
 private float weaveOffset;

 void Start()
 {
 animator = GetComponent<Animator>();
 }

 void Update()
 {
 Vector3 leftHandPos = leftController.position;
 Vector3 rightHandPos = rightController.position;
 Vector3 headPos = headset.position;
 Quaternion leftHandRot = leftController.rotation;
 Quaternion rightHandRot = rightController.rotation;
 Quaternion headRot = headset.rotation;

 // Use inverse kinematics to estimate the position of the hips
 animator.SetIKPositionWeight(AvatarIKGoal.LeftFoot, 1);
 animator.SetIKPositionWeight(AvatarIKGoal.RightFoot, 1);
 animator.SetIKRotationWeight(AvatarIKGoal.LeftFoot, 1);
 animator.SetIKRotationWeight(AvatarIKGoal.RightFoot, 1);
 animator.SetIKHintPositionWeight(AvatarIKHint.LeftKnee, 1);
 animator.SetIKHintPositionWeight(AvatarIKHint.RightKnee, 1);

 Vector3 leftFootPos = animator.GetIKPosition(AvatarIKGoal.LeftFoot);
 Vector3 rightFootPos = animator.GetIKPosition(AvatarIKGoal.RightFoot);
 Quaternion leftFootRot = animator.GetIKRotation(AvatarIKGoal.LeftFoot);
 Quaternion rightFootRot = animator.GetIKRotation(AvatarIKGoal.RightFoot);

 Vector3 hipsPos = (leftFootPos + rightFootPos) / 2f;

 // Determine the forward direction of the hips
 Vector3 headForward = headRot * Vector3.forward;
 Vector3 headUp = headRot * Vector3.up;
 Vector3 controllerForward = (leftHandRot * Vector3.forward + rightHandRot * Vector3.forward) / 2f;
 Vector3 hipsForward = (headForward + controllerForward).normalized;
 Vector3 hipsUp = (headUp + Vector3.up) / 2f;
 hips.rotation = Quaternion.LookRotation(hipsForward, hipsUp);

 // Adjust hip position if player is bobbing
 bobOffset += bobbingSpeed * Time.deltaTime;
 float bobAmount = Mathf.Sin(bobOffset) * bobbingAmount;
 hipsPos += hipsUp * bobAmount;

 // Adjust hip position if player is weaving
 weaveOffset += weavingSpeed * Time.deltaTime;
 float weaveAmount = Mathf.Sin(weaveOffset) * weavingAmount;
 Vector3 weaveDirection = Vector3.Cross(hipsForward, hipsUp);
 hipsPos += weaveDirection * weaveAmount;

 // Adjust hip position if player is bowing
 float angle = Vector3.Angle(headForward, Vector3.down);
 if (angle > 45f && !isBowing)
 {
 isBowing = true;
 hipsPos -= hipsForward * 0.2f;
 }
 else if (angle <= 45f && isBowing)
 {
 isBowing = false;
 hipsPos += hipsForward * 0.2f;
 }

 hips.position = hipsPos;
 }
}
