using UnityEngine;
using UnityEngine.XR;

[RequireComponent(typeof(Rigidbody))]
public class PhysicsLocomotion : MonoBehaviour
{
 [SerializeField] private float speed = 5.0f;
 [SerializeField] private float jumpForce = 5.0f;
 [SerializeField] private XRNode inputSource = XRNode.RightHand;

 private Rigidbody rb;
 private InputDevice device;
 private bool isGrounded;

 private Vector3 playerPositionLastFrame;
 private Vector3 playerPositionThisFrame;

 private void Start()
 {
 rb = GetComponent<Rigidbody>();
 device = InputDevices.GetDeviceAtXRNode(inputSource);

 // Initialize player positions
 playerPositionLastFrame = transform.position;
 playerPositionThisFrame = transform.position;
 }

 private void FixedUpdate()
 {
 // Add movement based on joystick input
 Vector2 inputAxis;
 if (device.TryGetFeatureValue(CommonUsages.primary2DAxis, out inputAxis))
 {
 Vector3 movement = new Vector3(inputAxis.x, 0, inputAxis.y) * speed;
 rb.AddForce(movement, ForceMode.Force);
 }

 // Add jump force if button is pressed and character is grounded
 if (isGrounded && device.TryGetFeatureValue(CommonUsages.primaryButton, out bool primaryButtonValue) && primaryButtonValue)
 {
 rb.AddForce(Vector3.up * jumpForce, ForceMode.Impulse);
 isGrounded = false;
 }

 // Add movement based on player real-world movement
 playerPositionThisFrame = transform.position;
 Vector3 delta = playerPositionThisFrame - playerPositionLastFrame;
 rb.MovePosition(rb.position + delta);
 playerPositionLastFrame = playerPositionThisFrame;
 }

 private void OnCollisionStay(Collision collision)
 {
 isGrounded = true;
 }
}
