 children?: SVGElementAttributes[];
}

function createSVGElement(attributes: SVGElementAttributes): SVGElement {
 let element = document.createElementNS("http://www.w3.org/2000/svg", attributes.tag);
 for (let key in attributes.attributes) {
 element.setAttribute(key, attributes.attributes[key]);
 }
 if (attributes.children) {
 for (let child of attributes.children) {
 element.appendChild(createSVGElement(child));
 }
 }
 return element;
