int n; // number of vertices

int main() {
 cin >> n;
 for (int i = 0; i < n; i++) {
 for (int j = 0; j <= i; j++) {
 int w;
 cin >> w;
 adj_matrix[i][j] = adj_matrix[j][i] = w;
 }
 }
