 $offset = 0;
 while ($offset < $total\_records) {
 $records = array\_slice($this->records, $offset, $this->chunk_size);
 $data = json\_encode($records);
 fwrite($file, $data);
 $offset += $this->chunk_size;
 }
 fclose($file);
}
