import org.apache.lucene.search.IndexSearcher;
import org.apache.lucene.search.ScoreDoc;
import org.apache.lucene.search.TopDocs;
import org.apache.lucene.store.Directory;
import org.apache.lucene.store.RAMDirectory;

import java.util.ArrayList;
import java.util.List;

public class SongSearchEngine {

 private Directory index;
 private IndexSearcher searcher;
 private static final String[] SEARCH_FIELDS = {"name", "author"};

 public SongSearchEngine() throws Exception {
 // Create an in-memory index for simplicity
 index = new RAMDirectory();
 IndexWriter writer = new IndexWriter(index, new StandardAnalyzer(), IndexWriter.MaxFieldLength.UNLIMITED);

 // Add some sample songs
 addSong(writer, "Bohemian Rhapsody", "Queen");
 addSong(writer, "Stairway to Heaven", "Led Zeppelin");
 addSong(writer, "The Sound of Silence", "Simon & Garfunkel");
 addSong(writer, "Blackbird", "The Beatles");
 addSong(writer, "Sweet Child O' Mine", "Guns N' Roses");

 writer.close();
 searcher = new IndexSearcher(index);
 }

 private void addSong(IndexWriter writer, String name, String author) throws Exception {
 Document doc = new Document();
 doc.add(new TextField("name", name, Field.Store.YES));
 doc.add(new TextField("author", author, Field.Store.YES));
 writer.addDocument(doc);
 }

 public List<String> search(String query, int numResults) throws Exception {
 MultiFieldQueryParser parser = new MultiFieldQueryParser(SEARCH_FIELDS, new StandardAnalyzer());
 TopDocs topDocs = searcher.search(parser.parse(query), numResults);

 List<String> results = new ArrayList<>();
 for (ScoreDoc scoreDoc : topDocs.scoreDocs) {
 Document document = searcher.doc(scoreDoc.doc);
 String name = document.get("name");
 String author = document.get("author");
 results.add(String.format("%s - %s", name, author));
 }
 return results;
 }

 public void close() throws Exception {
 searcher.getIndexReader().close();
 index.close();
 }

 public static void main(String[] args) throws Exception {
 SongSearchEngine searchEngine = new SongSearchEngine();
 List<String> results = searchEngine.search("queen", 3);
 for (String result : results) {
 System.out.println(result);
 }
 searchEngine.close();
 }
}
```
