const fs = require('fs');
const csv = require('csv-parser');
const mongoose = require('mongoose');
require('dotenv').config();

// Define the schema for the "trialDurations" collection
const trialDurationSchema = new mongoose.Schema({
 email: {
 type: String,
 required: true,
 },
});

// Create a model for the "trialDurations" collection
const TrialDuration = mongoose.model('trialDurations', trialDurationSchema);

// Connect to the MongoDB database using the Mongoose library
mongoose.connect(process.env.MONGO\_URI, {
 useNewUrlParser: true,
 useUnifiedTopology: true,
});

// Read the CSV file of emails and insert each email into the "trialDurations" collection
fs.createReadStream('emails.csv')
 .pipe(csv())
 .on('data', async (data) => {
 // Create a new document using the email value from the CSV row
 const document = new TrialDuration({ email: data.email });
 // Save the document to the "trialDurations" collection
 await document.save();
 console.log(`Inserted email: ${data.email}`);
 })
 .on('end', () => {
 console.log('Finished inserting emails.');
 // Close the database connection
 mongoose.connection.close();
 });
