// background.js

// Make API call to ChatGPT to retrieve conversation data
async function getConversationData() {
 const response = await fetch('https://api.openai.com/v1/engines/davinci/completions', {
 method: 'POST',
 headers: {
 'Content-Type': 'application/json',
 'Authorization': 'Bearer YOUR\_API\_KEY'
 },
 body: JSON.stringify({
 prompt: 'YOUR\_PROMPT',
 max\_tokens: 100
 })
 });
 const data = await response.json();
 return data.choices[0].text;
}

// Send conversation data to content script
chrome.runtime.onMessage.addListener(
 function(request, sender, sendResponse) {
 if (request.message === "get\_conversation") {
 getConversationData().then(conversation => {
 sendResponse({conversation: conversation});
 });
 return true; // required for sendResponse to work
 }
 });

// content.js

// Receive conversation data from background script
chrome.runtime.onMessage.addListener(
 function(request, sender, sendResponse) {
 if (request.conversation) {
 saveToNotion(request.conversation);
 }
 });

// Save conversation data to Notion
async function saveToNotion(conversation) {
 const response = await fetch('https://api.notion.com/v3/blocks/YOUR\_BLOCK\_ID/children', {
 method: 'POST',
 headers: {
 'Content-Type': 'application/json',
 'Authorization': 'Bearer YOUR\_API\_KEY'
 },
 body: JSON.stringify({
 children: [{
 type: 'text',
 text: {
 content: conversation
 }
 }]
 })
 });
 // Handle Notion API response
}
```
