```
// Fluent builder for form request and resource response
class OpenApiSpec
{
 protected $spec = [];

 public function createSpec(): self
 {
 $this->spec = [];
 return $this;
 }

 public function requestBody(): self
 {
 $this->spec['requestBody'] = [];
 return $this;
 }

 public function response(): self
 {
 $this->spec['response'] = [];
 return $this;
 }

 public function addProperties(array $properties): self
 {
 $this->spec['properties'] = $properties;
 return $this;
 }

 public function string(): self
 {
 $this->spec['type'] = 'string';
 return $this;
 }

 public function integer(): self
 {
 $this->spec['type'] = 'integer';
 return $this;
 }

 public function format(string $format): self
 {
 $this->spec['format'] = $format;
 return $this;
 }

 public function required(): self
 {
 $this->spec['required'] = true;
 return $this;
 }

 public function readOnly(): self
 {
 $this->spec['readOnly'] = true;
 return $this;
 }

 public function end(): array
 {
 return $this->spec;
 }
}
