```php
class ObjectRelationalHybrid {
 private $table\_name;
 private $field\_defs;
 private $records;

 public function \_\_construct($table\_name, $field\_defs) {
 $this->table_name = $table\_name;
 $this->field_defs = $field\_defs;

 $this->records = array();

 if (file\_exists($table\_name . '.txt')) {
 $file = file($table\_name . '.txt');
 foreach ($file as $line) {
 $record = array();
 $values = explode('|', trim($line));
 for ($i = 0; $i < count($field\_defs); $i++) {
 $record[$field\_defs[$i]] = $values[$i];
 }
 $this->records[] = $record;
 }
 }
 }

 public function \_\_get($field\_name) {
 if (in\_array($field\_name, $this->field_defs)) {
 return array\_column($this->records, $field\_name);
 } else {
 throw new Exception("Field $field\_name does not exist.");
 }
 }

 public function \_\_set($field\_name, $value) {
 if (in\_array($field\_name, $this->field_defs)) {
 $record = array\_combine($this->field_defs, array\_fill(0, count($this->field_defs), ''));
 $record[$field\_name] = $value;
 $this->records[] = $record;
 $this->save();
 } else {
 throw new Exception("Field $field\_name does not exist.");
 }
 }

 public function \_\_call($method\_name, $arguments) {
 if (substr($method\_name, 0, 6) == 'findBy') {
 $field\_name = lcfirst(substr($method\_name, 6));
 if (in\_array($field\_name, $this->field_defs)) {
 $records = array();
 foreach ($this->records as $record) {
 if ($record[$field\_name] == $arguments[0]) {
 $records[] = $record;
 }
 }
 return $records;
 } else {
 throw new Exception("Field $field\_name does not exist.");
 }
 } else {
 throw new Exception("Method $method\_name does not exist.");
 }
 }

 public function save() {
 $file = fopen($this->table_name . '.txt', 'w');
 foreach ($this->records as $record) {
 $values = array();
 foreach ($record as $value) {
 $values[] = $value;
 }
 fwrite($file, implode('|', $values) . "\n");
 }
 fclose($file);
 }
}
