function addImageDropButton() {
 // Create the button element
 var button = document.createElement("button");
 button.innerHTML = "Drop Image Here";
 button.style.position = "fixed";
 button.style.top = "10px";
 button.style.left = "10px";

 // Add the button to the page
 document.body.appendChild(button);

 // Handle the image drop event
 button.addEventListener("drop", function(event) {
 event.preventDefault();

 // Get the dropped image
 var image = event.dataTransfer.files[0];

 // Load the image
 var reader = new FileReader();
 reader.onload = function(event) {
 var imageData = event.target.result;

 // Create the canvas element
 var canvas = document.createElement("canvas");
 canvas.style.position = "fixed";
 canvas.style.top = "70px";
 canvas.style.left = "10px";
 canvas.style.border = "1px solid black";

 // Draw the image on the canvas
 var context = canvas.getContext("2d");
 var imageObj = new Image();
 imageObj.onload = function() {
 canvas.width = imageObj.width;
 canvas.height = imageObj.height;
 context.drawImage(imageObj, 0, 0);

 // If there is already a canvas, create a new canvas that fits both images
 if (document.getElementById("myCanvas")) {
 var oldCanvas = document.getElementById("myCanvas");
 canvas.width = oldCanvas.width + imageObj.width;
 canvas.height = Math.max(oldCanvas.height, imageObj.height);
 context.drawImage(oldCanvas, 0, 0);
 context.drawImage(imageObj, oldCanvas.width, 0);
 oldCanvas.parentNode.removeChild(oldCanvas);
 }

 canvas.id = "myCanvas";
 document.body.appendChild(canvas);
 };
 imageObj.src = imageData;
 };
 reader.readAsDataURL(image);
 });

 // Enable dragging and dropping on the button
 button.addEventListener("dragover", function(event) {
 event.preventDefault();
 });
}
